package Grind75.week7.find_all_anagrams_in_a_string_438;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

import javax.print.attribute.HashAttributeSet;

class Solution {

    public List<Integer> findAnagrams(String s, String p) {
        List<Integer> results = new ArrayList<>();

        if (s.length() < p.length()) return results;

        Map<Character, Integer> pDict = new HashMap<>();
        Map<Character, Integer> buffer = new HashMap<>();

        int tmp, ptr1, ptr2;
        char c;
        for (char c1 : p.toCharArray()) {
            pDict.merge(c1, 1, Integer::sum);
        }
            
        ptr1 = 0;
        ptr2 = 0;
        while (ptr2 < p.length()) {
            c = s.charAt(ptr2);
            buffer.merge(c, 1, Integer::sum);
            ptr2++;
        }

        while ( ptr1 <= s.length() && ptr2 <= s.length()) {
            if( buffer.equals(pDict)) {
                results.add(ptr1);
            }

            if (ptr1 == s.length() || ptr2 == s.length()){
                break;
            }

            c = s.charAt(ptr1);
            buffer.merge(c, -1, Integer::sum);
            if ( buffer.get(c) == 0 ){
                buffer.remove(c);
            }

            c = s.charAt(ptr2);
            buffer.merge(c, 1, Integer::sum);
            
            
            ptr1++;
            ptr2++;
        }


        return results;
    }
    public static void main(String[] args) {
        String s = "";
        String p = "";

        Solution sol = new Solution();

        System.out.println(sol.findAnagrams(s, p));
        System.out.println("Running find_all_anagrams_in_a_string_438...");
    }
}
